import { useState, useEffect } from 'react';
import { useRouter } from "next/router";
import CurrencyFormat from 'react-currency-format';

const Product = () => {
    const Router = useRouter();
    const { id } = Router.query;
    console.log(id)
    const [product, setProduct] = useState([]);
    useEffect(() => {
        const getAllProduct = async () => {
            const fetchAll = await fetch(`http://localhost:4000/v1/product/${id}`, {
                headers: {
                    'Content-Type': 'application/json',
                    'Access-Control-Allow-Origin': '*',
                }
            })
            const dataFetch = await fetchAll.json();
            setProduct(dataFetch);
        }
        getAllProduct()
    }, [id])
    return(
        <>
        <section className="row section-product">
            <article className="col-12 col-sm-12 col-lg-8 col-xl-8">
                <article className="col-12 content-img-prod-open">
                    <img src={product.img} alt={product.title}/>
                </article>
                <article className="col-12">
                    <h2>Descripción</h2><hr/>
                    <p>
                        {product.description}
                    </p>
                </article>
            </article>
            <article className="col-12 col-sm-12 col-lg-4 col-xl-4">
                <article className="col-12">
                    <h1 className="component-info-buy">{product.title}</h1>
                    <h4 className="text-primary component-info-buy">
                    <CurrencyFormat value={product.amount} displayType={'text'} thousandSeparator={true} prefix={'$'}/>
                    </h4>
                    <strike>
                        <h5 className={`text-secondary component-info-buy ${(product.discount === 0) ? 'd-none' : ''}`}>
                            <CurrencyFormat value={(product.amount * product.discount) / 100} displayType={'text'} thousandSeparator={true} prefix={'$'}/>
                        </h5>
                    </strike>
                </article>
                <article className="col-12">
                    <p className="component-info-buy">Pagá al contado o en cuotas con 
                    <span className="text-primary"> <strong>Mercado Pago</strong></span></p>
                    <article className='col-12 p-1 bg-primary mb-2 text-light'>
                        <ol>
                            <li className='p-1'>Hacé click en <small>"Compralo en un instante!"</small></li>
                            <li className='p-1'>Hacé el pago con Mercado Pago</li>
                            <li className='p-1'>Sacale una foto o screenshot al comprobante de pago</li>
                            <li className='p-1'><a href='#' className='btn btn-success'>Envíalo por WhatsApp!</a></li>
                        </ol>
                    </article>
                    <a href={product.payment} target='_blank' className="btn btn-buy-now text-light d-block">
                        Compralo en un instante!
                    </a>
                </article>
            </article>
        </section>
        </>
    )
}

export default Product;
