import { useState } from 'react';
import { useEffect } from 'react';
import SellsPending from './sells.pending.component';
import moment from 'moment';

const TransactionsComp = prop => {
    const [transactionMetrics, setTransactionMetrics] = useState([]);
    const [valueDate, setValueDate] = useState(moment().format('YYYY-MM-DD'));

    const handlerChangeFilterDate = e => setValueDate(moment(e.target.value).format('YYYY-MM-DD'))

    useEffect(() => {
        const handlerFetchMetrics = async () => {
            // fetch metrics
            const getFetchMetrics = await fetch(`${process.env.API_PATH}/v1/product/transaction/sells/metrics`, {
                headers: {
                    'Content-Type': 'application/json',
                    'Access-Control-Allow-Origin': '*'
                }
            });
            const resFetchMetrics = await getFetchMetrics.json();
            setTransactionMetrics(resFetchMetrics);
        }
        handlerFetchMetrics();
    }, [prop]);
    return(
        <>
        <article className="content-metrics-header-mybusiness col-12 col-sm-12 col-lg-12 col-xl-10">
            <ul>
                <li title='Pago exitoso'>
                    <svg 
                        xmlns="http://www.w3.org/2000/svg" 
                        fill="currentColor" 
                        className="bi bi-bag-check text-primary" 
                        viewBox="0 0 16 16">
                        <path 
                            fillRule="evenodd" 
                            d="M10.854 8.146a.5.5 0 0 1 0 .708l-3 3a.5.5 0 0 1-.708 0l-1.5-1.5a.5.5 0 0 1 .708-.708L7.5 10.793l2.646-2.647a.5.5 0 0 1 .708 0z"
                        />
                        <path 
                            d="M8 1a2.5 2.5 0 0 1 2.5 2.5V4h-5v-.5A2.5 2.5 0 0 1 8 1zm3.5 3v-.5a3.5 3.5 0 1 0-7 0V4H1v10a2 2 0 0 0 2 2h10a2 2 0 0 0 2-2V4h-3.5zM2 5h12v9a1 1 0 0 1-1 1H3a1 1 0 0 1-1-1V5z"
                        />
                    </svg> 
                    <span className='text-primary d-block'>{transactionMetrics.success}</span>
                </li>
                <li title='Pago pendiente'>
                    <svg 
                        xmlns="http://www.w3.org/2000/svg" 
                        fill="currentColor" 
                        className="bi bi-bag-dash text-secondary" 
                        viewBox="0 0 16 16">
                        <path 
                            fillRule="evenodd" 
                            d="M5.5 10a.5.5 0 0 1 .5-.5h4a.5.5 0 0 1 0 1H6a.5.5 0 0 1-.5-.5z"
                        />
                        <path 
                            d="M8 1a2.5 2.5 0 0 1 2.5 2.5V4h-5v-.5A2.5 2.5 0 0 1 8 1zm3.5 3v-.5a3.5 3.5 0 1 0-7 0V4H1v10a2 2 0 0 0 2 2h10a2 2 0 0 0 2-2V4h-3.5zM2 5h12v9a1 1 0 0 1-1 1H3a1 1 0 0 1-1-1V5z"
                        />
                    </svg> 
                    <span className='text-secondary d-block'>{transactionMetrics.pending}</span>
                </li>
            </ul>
        </article>
        <article className="content-metrics-header-mybusiness col-2-col-sm-2 col-lg-2 col-xl-2">
            <span className="badge bg-primary text-light mb-2">Filtrar por fecha</span>
            {
                transactionMetrics.latest_sell 
                && <input
                        className='d-block form-control'
                        onChange={handlerChangeFilterDate} 
                        type="date" 
                        min={moment(transactionMetrics.latest_sell[0].create_at).format('YYYY-MM-DD')} 
                        max={moment().format('YYYY-MM-DD')}
                        value={valueDate}
                />
            }
        </article>
        <article className='col-12 col-sm-12 col-lg-12 col-xl-12'>
            <SellsPending/>
        </article>
        </>
    )
}

export default TransactionsComp;
