import { useState, useEffect } from 'react';
import { useRouter } from "next/router";
import CurrencyFormat from 'react-currency-format';

const Product = () => {
    const Router = useRouter();
    const { id } = Router.query;
    console.log(id)
    const [product, setProduct] = useState([]);
    const [acceptPaymentSwitch, setAcceptPaymentSwitch] = useState('d-none');
    const handlerAcceptPaymentSwitch = e => {
        if(acceptPaymentSwitch === 'd-none'){
            e.target.classList.toggle('btn-warning');
            e.target.value = 'Cerrar';
            setAcceptPaymentSwitch('d-block');
        }else{
            e.target.classList.toggle('btn-warning');
            e.target.value = 'Pagar con Mercado Pago!';
            setAcceptPaymentSwitch('d-none');
        }
    }
    useEffect(() => {
        const getAllProduct = async () => {
            const fetchAll = await fetch(`http://localhost:4000/v1/product/${id}`, {
                headers: {
                    'Content-Type': 'application/json',
                    'Access-Control-Allow-Origin': '*',
                }
            })
            const dataFetch = await fetchAll.json();
            setProduct(dataFetch);
        }
        getAllProduct()
    }, [id])
    return(
        <>
        <section className="row section-product">
            <article className="col-12 col-sm-12 col-lg-8 col-xl-8">
                <article className="col-12 content-img-prod-open">
                    <img src={product.img} alt={product.title}/>
                </article>
                <article className="col-12">
                    <h2>Descripción</h2><hr/>
                    <ul type='none'>
                        <li>
                            - Categoría: <span className='text-dark'><strong>{product.type}</strong></span><br/>
                        </li>
                        <li>
                            - Nombre del negocio: <span className='text-dark'><strong>{product.business}</strong></span>
                        </li>
                        <li>
                        - Ubicación: <span className='text-dark'><strong>{product.location}</strong></span>
                        </li>
                    </ul><hr/>
                    <p>
                        {product.description}
                    </p>
                </article>
            </article>
            <article className="col-12 col-sm-12 col-lg-4 col-xl-4">
                <article className="col-12">
                    <h1 className="component-info-buy">{product.title}</h1>
                    <h4 className="text-primary component-info-buy">
                    {(product.amount !== 0) 
                    ? <CurrencyFormat value={product.amount} displayType={'text'} thousandSeparator={true} prefix={'$'}/>
                    : <span className='text-success'>GRATIS</span>}
                    </h4>
                    <strike>
                        <h5 className={`text-secondary component-info-buy ${(product.discount === 0) ? 'd-none' : ''}`}>
                            <CurrencyFormat value={(product.amount * product.discount) / 100} displayType={'text'} thousandSeparator={true} prefix={'$'}/>
                        </h5>
                    </strike>
                    <p className='col-12 col-sm-12 col-lg-6 col-xl-6 p-0 m-0'>
                        <span className={`${(!product.shipping) ? 'd-none' : 'd-block'} shipping-open bg-primary text-light`}>
                            Lo recibís en tu domicilio
                        </span>
                        <span className={`shipping-open bg-success text-light d-block`}>
                            Envío gratis
                        </span>
                    </p>
                </article>
                <article className="col-12">
                    <p className="component-info-buy">Pagá al contado o en cuotas con 
                    <span className="text-primary"> <strong>Mercado Pago</strong></span></p>
                    <p>
                        <span className='bg-info p-2 text-light'>
                            Se encuentra en <strong>{product.location}</strong>
                        </span>
                    </p>
                    <article className='col-12 p-1 content-method-buy-steps mb-2 text-dark'>
                        <ol type='1'>
                            <li className='p-1'>Hacé click en <small>"Compralo en un instante!"</small></li>
                            <li className='p-1'>Hacé el pago con Mercado Pago</li>
                            <li className='p-1'>Sacale una foto o screenshot al comprobante de pago</li>
                            <li className='p-1'><a href='#' className='btn btn-success'>Envíalo por WhatsApp!</a></li>
                        </ol>
                    </article>
                    <article className={`col-12 accept-payment ${acceptPaymentSwitch}`}>
                        <article className='content--btn-payment bg-warning p-5'>
                            <article className="btn-content">
                                <p>Finaliza tu pago, y te lo enviamos a tu casa!</p>
                                <a href={product.payment} target='_blank' className="btn btn-primary text-light d-block">
                                    Pagar con Mercado Pago!
                                </a>
                            </article>       
                        </article>
                    </article>
                    <input onClick={handlerAcceptPaymentSwitch} type='button' className="btn btn-buy-now text-light d-block col-12" value='Compralo en un instante!'/>
                </article>
            </article>
        </section>
        </>
    )
}

export default Product;
