import Link from 'next/link';
import { useState } from 'react';
import { useEffect } from 'react';

const BlockProduct = prop => {
    const [product, setProduct] = useState([]);
    useEffect(() => {
        const getAllProduct = async () => {
            const fetchAll = await fetch('http://localhost:4000/v1/product/category/all', {
                headers: {
                    'Content-Type': 'application/json',
                    'Access-Control-Allow-Origin': '*',
                }
            })
            const dataFetch = await fetchAll.json();
            setProduct(dataFetch);
            console.log(dataFetch);
        }
        getAllProduct()
    }, [])


    return(
        <>
        <article className="title-block-prod-home col-12 col-sm-12 col-lg-12 col-xl-12">
            <h1 className="bg-dark">{prop.section}</h1>
        </article>
        {
            product ? product.map((prod, i) => (
                <Link href='/[name]/[id]' as='/nombre_producto1/ihj234hj554333ksodfu3u3'>
                    <a className="card col-12 col-sm-12 col-lg-2 col-xl-2">
                        <article className="card-body">
                            <img className="card-img" src="/img/1.jpg" alt="img"/>
                            <p className="card-title">Product</p>
                            <p className="card-price">$15.000</p>
                            <p className="d-none card-description">
                                Lorem ipsum dolor sit amet, consectetur
                                adipisicing elit. Repellendus aperiam totam, 
                                omnis minus nostrum facilis.
                            </p>
                        </article>
                    </a>
                </Link>
            )) : <h3 className='p-2 bg-secondary'>No hay art√≠culos disponibles</h3>
        }
        </>
    )
}

export default BlockProduct;
