                <li>Precio: <span className='text-success'><strong>{ transaction.product.amount }</strong></span></li>
                <li className={transaction.status_transaction === 'pending' ? 'text-danger' : 'text-success'}>
                    <strong>{ transaction.status_transaction === 'pending' ? 'Pendiente' : 'Aprobado'}</strong>
                </li>
