            <input onClick={handlerSwitchCardView} type="button" className='btn btn-secondary' value="Ver descripci贸n del producto"/>
            <input onClick={handlerSwitchCardViewCustomer} type="button" className='btn btn-info' value="Ver descripci贸n del cliente"/>
            <ul className='content-description-prod-transaction d-none' ref={cardViewMore}>
                <li>T铆tulo: <strong>{ }</strong></li>
