import { useState, useEffect } from 'react';
import { useRouter } from "next/router";

const Product = () => {
    const Router = useRouter();
    const { id } = Router.query;
    const [product, setProduct] = useState([]);
    useEffect(() => {
        const getAllProduct = async () => {
            const fetchAll = await fetch(`http://localhost:4000/v1/product/${id}`, {
                headers: {
                    'Content-Type': 'application/json',
                    'Access-Control-Allow-Origin': '*',
                }
            })
            const dataFetch = await fetchAll.json();
            setProduct(dataFetch);
        }
        getAllProduct()
    }, [id])
    return(
        <>
        <section className="row section-product">
            <article className="col-12 col-sm-12 col-lg-8 col-xl-8">
                <article className="col-12 content-img-prod-open">
                    <img src="../../img/5.jpg" alt="product"/>
                </article>
                <article className="col-12">
                    <h2>Descripción</h2><hr/>
                    <p>
                        Lorem ipsum dolor sit amet, consectetur adipisicing elit. 
                        Fugiat dignissimos sed necessitatibus consequuntur voluptatum nam. 
                        Velit distinctio repudiandae, earum quibusdam vitae quia asperiores 
                        ex numquam vero ipsa hic eius sapiente?
                    </p>
                </article>
            </article>
            <article className="col-12 col-sm-12 col-lg-4 col-xl-4">
                <article className="col-12">
                    <h1 className="component-info-buy">Product</h1>
                    <h4 className="text-primary component-info-buy">$14.000</h4>
                </article>
                <article className="col-12">
                    <p className="component-info-buy">Pagá al contado o en cuotas con 
                    <span className="text-primary"> <strong>Mercado Pago</strong></span></p>
                    <input className="btn btn-buy-now d-block" type="button" value="Compralo en un instante!"/>
                </article>
            </article>
        </section>
        </>
    )
}

export default Product;
