import { useRef } from "react";

const { useState } = require("react")
const { useEffect } = require("react")

const ColSellPending = prop => {
    const [product, setProduct] = useState([]);
    const cardViewMore = useRef(null);
    useEffect(() => {
        const getAllProduct = async id => {
            const fetchAll = await fetch(`http://localhost:4000/v1/product/${id}`, {
                headers: {
                    'Content-Type': 'application/json',
                    'Access-Control-Allow-Origin': '*'
                }
            })
            const dataFetch = await fetchAll.json();
            setProduct(dataFetch);
        }
        getAllProduct(prop.idProduct)
    }, [prop]);
    const handlerSwitchCardView = e => {
        cardViewMore.current.classList.toggle('d-block');
        e.target.classList.toggle('btn-danger');
        e.target.value = (e.target.value === 'Ver descripción') ? 'Cerrar' : 'Ver descripción'
    }
    return(
        <article className='col-12 col-sm-12 col-lg-12 col-xl-12 col-sells'>
            <ul>
                <li>Cliente: <span className='text-dark'><strong>{prop.id_client}</strong></span></li>
                <li>Canal: <span className='text-primary'> <strong>{prop.token_influencer}</strong></span></li>
                <li>Precio: <span className='text-success'><strong>$23,000</strong></span></li>
                <li>
                    <input type="button" value="Acreditado" className="btn btn-primary"/>
                    <input type="button" value="Rechazado" className="btn btn-warning"/>
                </li>
            </ul>
            <input onClick={handlerSwitchCardView} type="button" className='btn btn-info' value="Ver descripción"/>
            <ul className='content-description-prod-transaction d-none' ref={cardViewMore}>
                <li>Título: {product.title}</li>
                <li className='desc-card-view-transaction'>Descripción: <p className='p-3 d-inline'><strong>{product.description}</strong></p></li>
                <li>Localidad: <strong>{product.location}</strong></li>
                <li>Negocio: <strong>{product.business}</strong></li>
                <li>Categoría <strong>{product.type}</strong></li>
            </ul>
        </article>
    )
}

export default ColSellPending;
