const RemoveProduct = prop => {
    const handlerFetchRemove = async () => {
        const getFetch = await fetch(`${process.env.API_PATH}/v1/product/${prop.product._id}`, { 
            body: JSON.stringify({img: prop.product.img}),
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json',
                'Access-Control-Allow-Origin': '*'
            }
        });
        const resFetch = await getFetch.json();
        return resFetch;
    }
    const handlerRemove = () => {
        handlerFetchRemove()
        .then(result => {
            if(result.removed){
                window.location = '/';
            }else{
                alert('Ha ocurrido un error. Intente nuevamente');
            }
        })
        console.log(prop.product.img)
    }
    return(
        <>
        <button onClick={handlerRemove} className='btn-remove-product-open btn-danger btn' type='button'>
            <svg width="1em" height="1em" viewBox="0 0 16 16" className="ml-2 bi bi-trash" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
                <path d="M5.5 5.5A.5.5 0 0 1 6 6v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5zm2.5 0a.5.5 0 0 1 .5.5v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5zm3 .5a.5.5 0 0 0-1 0v6a.5.5 0 0 0 1 0V6z"/>
                <path fillRule="evenodd" d="M14.5 3a1 1 0 0 1-1 1H13v9a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V4h-.5a1 1 0 0 1-1-1V2a1 1 0 0 1 1-1H6a1 1 0 0 1 1-1h2a1 1 0 0 1 1 1h3.5a1 1 0 0 1 1 1v1zM4.118 4L4 4.059V13a1 1 0 0 0 1 1h6a1 1 0 0 0 1-1V4.059L11.882 4H4.118zM2.5 3V2h11v1h-11z"/>
            </svg>
        </button>
        </>
    )
}

export default RemoveProduct;
