import Link from 'next/link';
import { useState } from 'react';
import { useEffect } from 'react';
import CurrencyFormat from 'react-currency-format';

const ProductAll = prop => {
    const [product, setProduct] = useState([]);
    useEffect(() => {
        const getAllProduct = async () => {
            const fetchAll = await fetch(`${process.env.API_PATH}/v1/product/category/${prop.api}`, {
                headers: {
                    'Content-Type': 'application/json',
                    'Access-Control-Allow-Origin': '*',
                }
            })
            const dataFetch = await fetchAll.json();
            setProduct(dataFetch);
            console.log(dataFetch);
        }
        getAllProduct()
    }, [prop.api])


    return(
        <>
        <article className={`col-12 col-sm-12 col-lg-12 col-xl-12 bg-dark ${product ? '' : 'd-none'}`}>
            <p className='title-product-all text-light'>{(prop.api === 'all') ? 'Todos los productos' : prop.api}</p>
        </article>
        {
             product ? product.map((prod, i) => (
                <Link key={i} href={`${prod.title}/${prod._id}`} as={`${prod.title}/${prod._id}`}>
                    <a className="card-product-all card col-12 col-sm-12 col-lg-12 col-xl-12">
                        <article className="card-body">
                            <img src={prod.img} alt={prod.title}/>
                            <p className="card-title">{prod.title}</p>
                            <p className="card-price">
                            {(prod.amount !== 0) 
                            ? <CurrencyFormat value={prod.amount} displayType={'text'} thousandSeparator={true} prefix={'$'}/>
                            : <span className='text-success'>GRATIS</span>}
                            </p>
                            <p className="d-none card-description">
                                {prod.description}
                            </p>
                        </article>
                    </a>
                </Link>
            )) : <h3 className='p-2 m-auto not-products-title'>No hay art√≠culos disponibles</h3>
        }
        </>
    )
}

export default ProductAll;
