import { useState } from "react";
import { useCookies } from 'react-cookie';

const AddProduct = props => {
    const [cookies, setCookie] = useCookies(['user']);
    const [imageProd, setImageProd] = useState('/img/img-not-found.png')
    
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [amount, setAmount] = useState('');
    const [discount, setDiscount] = useState('');
    const [shipping, setShipping] = useState('');
    const [business, setBusiness] = useState('');
    const [location, setLocation] = useState('');
    const [type, setType] = useState('');

    const handleAddProduct = async product => {
        await fetch('http://localhost:4000/v1/auth', {
            body: JSON.stringify({token: await cookies.user.token}), 
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Access-Control-Allow-Origin': '*',
                'token': cookies.user.token
            }
        })
        .then(res => res.json())
        .then(async success => {
            if(success.auth){
                await fetch('http://localhost:4000/v1/product', {
                    body: JSON.stringify(product), 
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        'Access-Control-Allow-Origin': '*',
                        'token': cookies.user.token
                    }
                })
                .then(successAdd => successAdd.json())
                .then(productAdd = console.log(productAdd))
                .catch(errAdd => console.error(errAdd))
            }else{
                console.log(success)
            }
        })
        .catch(err => console.error(err))
    }
    const handlerSubmit = e => {
        e.preventDefault()
        const dataAddProduct = {
            title: title,
            description: description,
            img: 'imageProd',
            amount: amount,
            discount: discount,
            shipping: shipping,
            business: business,
            location: location,
            type: type
        }
        handleAddProduct(dataAddProduct)
    }
    
    const handleInputTitle = e => {
        setLocation(e.target.value)
        
    }
    const handleInputDescription = e => {
        setDescription(e.target.value)
        
    }
    const handleInputAmount = e => {
        setAmount(e.target.value)
        
    }
    const handleInputDiscount = e => {
        setDiscount(e.target.value)
        
    }
    const handleInputShipping = e => {
        setShipping(e.target.value)
        
    }
    const handleInputBusiness = e => {
        setBusiness(e.target.value)
        
    }
    const handleInputLocation = e => {
        setTitle(e.target.value)
        
    }
    const handleInputType= e => {
        setType(e.target.value)
        
    }
    const imgPreviewProd = (e) => {
        let reader = new FileReader()
        reader.onloadend = () => {
            setImageProd(reader.result);
        }
        if (e.target.files[0]){
            reader.readAsDataURL(e.target.files[0]);
        }else{
            setImageProd('');
        }
    }
    return(
        <>
         <article className="col-12 col-sm-12 col-lg-4 col-xl-4">
            <article className='col-12'>
                <h4 className='d-inline-flex'>Agregar un producto</h4>
                <svg width="1em" height="1em" viewBox="0 0 16 16" className="ml-2 bi bi-plus-circle" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
                    <path fillRule="evenodd" d="M8 15A7 7 0 1 0 8 1a7 7 0 0 0 0 14zm0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16z"/>
                    <path fillRule="evenodd" d="M8 4a.5.5 0 0 1 .5.5v3h3a.5.5 0 0 1 0 1h-3v3a.5.5 0 0 1-1 0v-3h-3a.5.5 0 0 1 0-1h3v-3A.5.5 0 0 1 8 4z"/>
                </svg>
                <form onSubmit={handlerSubmit}>
                    <small className='p-2 m-0'>Título <span className='text-danger'>*</span></small>
                    <input onChange={handleInputTitle} className='form-control d-block' type="text" placeholder='Título'/>
                    <small className='p-2 m-0'>Seleccionar una imágen <span className='text-danger'>*</span></small>
                    <input onChange={handleInputTitle} type="file" onChange={imgPreviewProd} className='mt-2 mb-2' accept='image/*'/>
                    <article style={{backgroundImage: `url('${imageProd}')`}} className='img-preview-prod'></article>
                    <small className='p-2 m-0'>Categoría <span className='text-danger'>*</span></small>
                    <select onChange={handleInputType} className='form-control d-block'>
                        <option value="null">-- Seleccionar una opción --</option>
                        <option value="accesorios">Accesorios</option>
                        <option value="belleza">Belleza</option>
                        <option value="electrodomésticos">Electrodomésticos</option>
                        <option value="fotografía">Fotografía</option>
                        <option value="gym">Gym</option>
                        <option value="indumentaria">Indumentaria</option>
                        <option value="joyas">Joyas</option>
                        <option value="muebles">Muebles</option>
                        <option value="música">Música</option>
                        <option value="mascotas">Mascotas</option>
                        <option value="suplementos">Suplementos</option>
                        <option value="Tecnología">Tecnología</option>
                    </select>
                    <small className='p-2 m-0'>Precio <span className='text-danger'>*</span></small>
                    <input onChange={handleInputAmount} className='form-control d-block' type="text" placeholder='Precio'/>
                    <small className='p-2 m-0'>Descuento <span className='text-danger'>*</span></small>
                    <input onChange={handleInputDiscount} className='form-control d-block' type="text" placeholder='Descuento'/>
                    <small className='p-2 m-0'>Descripción <span className='text-danger'>*</span></small>
                    <input onChange={handleInputDescription} className='form-control d-block' type="text" placeholder='Descripción'/>
                    <small className='p-2 m-0'>Link Mercado Pago <span className='text-danger'>*</span></small>
                    <input onChange={handleInputTitle} className='form-control d-block' type="text" placeholder='Link Mercado Pago'/>
                    <small className='p-2 m-0'>Empresa <span className='text-danger'>*</span></small>
                    <input onChange={handleInputBusiness} className='form-control d-block' type="text" placeholder='Empresa'/>
                    <small className='p-2 m-0'>Localidad <span className='text-danger'>*</span></small>
                    <input onChange={handleInputLocation} className='form-control d-block' type="text" placeholder='Localidad'/>
                    <small className='p-2 m-0'>Envíos <span className='text-danger'>*</span></small>
                    <select onChange={handleInputShipping} className='mb-2 form-control d-block'>
                        <option value="null">-- Seleccionar una opción --</option>
                        <option value="true">Acepto envíos</option>
                        <option value="false">No acepto envíos</option>
                    </select>
                    <input onChange={handleInputTitle} type="submit" value="Agregar" className='form-control d-block btn btn-primary'/>
                </form>
            </article>
        </article>
        </>
    )
}

export default AddProduct;
