import { useRef } from "react";
import { useEffect } from "react";
import { useState } from "react";
import { useCookies } from "react-cookie";

const InputTokenInfluencer = () => {
    const [tokenValue, setTokenValue] = useState('');
    const [btnValueToken, setBtnValueToken] = useState('Verificar');
    const [cookies, setCookie] = useCookies(['user']);
    const btnSubmit = useRef(null);
    useEffect(() => {
        if(cookies.user.discount_token){
            btnSubmit.current.classList.add('bg-success');
            setBtnValueToken('Verificado!')
        }
    })

    const handlerInputToken = e => {
        setTokenValue(e.target.value);
    }
    const AuthToken = async token_payment => {
        const fetchToken = await fetch(`${process.env.API_PATH}/v1/influencer/${token_payment}`);
        const getFetch = await fetchToken.json();
        return getFetch;
    }
    const handlerSubmit = e => {
        e.preventDefault()
        AuthToken(tokenValue)
        .then(success => {
            console.log(success)
            if(success.token_verify === 1){
                btnSubmit.current.classList.toggle('bg-success');
                setBtnValueToken('Verificado!')
                let userCookieCredentials = cookies.user;
                userCookieCredentials.discount_token = tokenValue;
                setCookie('user', userCookieCredentials, { maxAge: 86400 });
            }else{
                btnSubmit.current.classList.toggle('bg-danger');
                setBtnValueToken('Rechazado!')
            }
        })
    }
    return(
        <>
        <form className='frm-token' onSubmit={handlerSubmit}>
            <input onChange={handlerInputToken} className="code-ref-input form-control" 
                type="text" 
                placeholder='#Código de referido' 
                aria-label="#Código de referido"/>
            <input ref={btnSubmit} type="submit" className='btn btn-search' value={btnValueToken}/>
        </form>
        </>
    )
}

export default InputTokenInfluencer;
